                    spc = (check - word_length) / space;
                    extra = (check - word_length) % space;
                }
                for (int j = i; j <= end; j++)
                {
                    s += w[j];
                    for (int k = 0; k < spc && j != end; k++)
                    {
                        s += " ";
                    }
                    if (extra)
                    {
                        extra--;
                        s += " ";
                    }
                }
                if (space == 0)
                {
                    int k = s.size();
                    for (int j = 1; j <= (check - k); j++)
