    public List<String> fullJustify(String[] words, int maxWidth) {    	

        List<String> results = new LinkedList<>();   

        if(words == null) {
            return results;
        }        

        int[][] costsMatrix = createCostMatrix(words, maxWidth);

        int len = words.length;

        int[] minCosts = new int[len];
        int[] paths = new int[len];

        Arrays.fill(minCosts, Integer.MAX_VALUE);

        for(int i = len-1; i >= 0; i--) {

            minCosts[i] = costsMatrix[i][len-1];
            paths[i] = len;         

            for(int j = i+1; j < len; j++) {

                if(costsMatrix[i][j-1] != Integer.MAX_VALUE &&
                    minCosts[j] + costsMatrix[i][j-1] < minCosts[i]) {

                    minCosts[i] = minCosts[j] + costsMatrix[i][j-1];
                    paths[i] = j;                            
                }
            }            
        }

        JustifierIterator iter = new JustifierIterator(words, paths, maxWidth);

        while(iter.hasNext()) {
            results.add(iter.next());
        }

        return results;        
    }

    private int[][] createCostMatrix(String[] words, int maxWidth) {

        int len = words.length;
        int[][] costMatrix = new int[len][len];

        for(int row = 0; row < len-1; row++) {

            int usedSpace = 0;
            Arrays.fill(costMatrix[row], Integer.MAX_VALUE);

            for(int col = row; col < len; col++) {

            	usedSpace += words[col].length();
                if(usedSpace <= maxWidth) {
                	if(col == len-1) {
                		costMatrix[row][col] = 0;
                	} else {                	
	                	int unusedSpace = maxWidth - usedSpace;
	                    costMatrix[row][col] = unusedSpace * unusedSpace;
                	}
                } else {
                	if(col > 0) {
                		costMatrix[row][col-1] = 0;
                	}
                    break;
                }
                usedSpace++;
            }                        
        }

        costMatrix[len-1][len-1] = 0;

        return costMatrix;
    }

    private class JustifierIterator {

        String[] words;
        int[] paths;
        int maxWidth;

        int pathsIndex;

        public JustifierIterator(String[] words, int[] paths, int maxWidth) {
        	this.paths = paths;
            this.words = words;
            this.maxWidth = maxWidth;
        }

        public boolean hasNext() {
            return pathsIndex < words.length;
        }

        public String next() {

            int numWords = paths[pathsIndex] - pathsIndex; 
            int spacesCount = calculateNumSpaces(pathsIndex, numWords);

            String result = generateSentence(pathsIndex, numWords, spacesCount);
            pathsIndex = paths[pathsIndex];

            return result;
        }

        private int calculateNumSpaces(int initialIndex, int numWords) {

            int numChars = 0;

            for(int i = 0; i < numWords; i++) {

                numChars += words[initialIndex + i].length();
            }

            return maxWidth - numChars;
        }

        private String generateSentence(int initialIndex, int numWords, int spacesCount) {

            StringBuilder sentence = new StringBuilder();

            if(numWords == 1) {
                sentence.append(words[initialIndex]);
                sentence.append(generateSpaces(maxWidth-words[initialIndex].length()));
                return sentence.toString();
            }

            if(initialIndex + numWords == words.length) {

                int len = 0;

                for(int i = 0; i < numWords; i++) {

                    sentence.append(words[initialIndex+i]);
                    len += words[initialIndex+i].length();

                    if(len < maxWidth) {
                        sentence.append(" ");
                        len++;
                    }
                }                
                sentence.append(generateSpaces(maxWidth-len));
                return sentence.toString();
            }

            int evenDivision = spacesCount / (numWords-1);
            int leftOverSpaces = spacesCount % (numWords-1);

            for(int i = 0; i < numWords-1; i++) {

                int spacesToAdd = evenDivision;
                if(leftOverSpaces > 0) {
                    spacesToAdd++;
                    leftOverSpaces--;
                }

                sentence.append(words[initialIndex+i]);
                sentence.append(generateSpaces(spacesToAdd));
            }

            sentence.append(words[initialIndex+numWords-1]);

            return sentence.toString();
        }

        private char[] generateSpaces(int spacesCount) {

            char[] blankSequence = new char[spacesCount];
            Arrays.fill(blankSequence, ' ');

            return blankSequence;
        }        
    }

