```
public class Line {
    private readonly int TotalChars;
    private int CharsLeft;
    public List<string> Words;

    public int Spaces {
        get {
            int allWordsLen = Words.Aggregate(0, (prev, next) => prev + next.Length);
            return TotalChars - allWordsLen - (Words.Count - 1);
        }
    }
    public Line(int avail) {
        TotalChars = avail;
        CharsLeft = avail;

        Words = new List<string>();
    }
    public bool Consume(string word) {
        if (word.Length <= CharsLeft) {
            CharsLeft -= word.Length + 1; //+1 for a space
            Words.Add(word);
            return true;
        }

        return false;
    }

    public override string ToString() {        
        return Words.Aggregate((prev, next) => string.Format("{0} {1}", prev, next));  
    }
}

public class Solution {

    public static string Justify(Line line) {
        //We want to distribute the available spaces into a few different buckets.
        //To be correct, the last bucket must always contain zero spaces.
        //This is why I'm % by buckets.Length - 1 in the loop

        int[] buckets = new int[line.Words.Count];
        int totalToDistribute = line.Spaces;
        int index = 0;

        int totalBuckets = Math.Max(buckets.Length - 1, 1);
        while (totalToDistribute > 0) {
            buckets[index]++;
            totalToDistribute--;
            index = (index + 1) % totalBuckets;
        }

        var withPadding = line.Words.Zip(buckets, (word, bucket) => {
            return string.Concat(word, new String(' ', bucket));
        });

        return withPadding.Aggregate((first, second) => string.Format("{0} {1}", first, second));
    }

    public static string LeftJustify(Line line) {
        return string.Concat(line.ToString(), new String(' ', line.Spaces));
    }

    public IList<string> FullJustify(string[] wordsArray, int maxWidth) {
        Queue<string> words = new Queue<string>(wordsArray);
        List<Line> lines = new List<Line>();

        while (words.Count > 0) {
            Line line = new Line(maxWidth);
            while (words.Count > 0 && line.Consume(words.Peek())) {
                words.Dequeue();
            }
            lines.Add(line);
        }

        //We justify all the lines until the very last
        List<string> result = new List<string>();
        for (int i = 0; i < lines.Count - 1; i++) {
            result.Add(Justify(lines[i]));
        }
        //And then do a special left justify on the last.
        result.Add(LeftJustify(lines.Last()));
        return result;
    }
}
