class Solution {
public:

    string makeLine(vector<string>&v, int toFill){
        string res = "";
        // One word line
        if(v.size() == 1){
            res += v[0];
            while(toFill--) res += ' ';
            return res;
        }

        //Multiple word line
        int space = toFill / (v.size()-1);
        int extras = toFill % (v.size()-1);
        res += v[0];
        for(int i=1;i<v.size();i++){
            for(int j=0;j<space;j++) res += ' ';
            if(extras){
                extras --;
                res += ' ';
            }
            res += v[i];
        }
        return res;
    }

     string makeLastLine(vector<string>&v, int toFill){
        string res = v[0];
        for(int i=1;i<v.size();i++){
            toFill --;
            res += ' ';
            res += v[i];
        }
        while(toFill --) res += ' ';
        return res;
     }

    vector<string> fullJustify(vector<string>& words, int maxWidth) {
        vector<string> res;
        vector<string> taken;
        int lineLen = 0;
        for(auto word: words){
            if(lineLen+word.size()+taken.size() > maxWidth){
                res.push_back(makeLine(taken, maxWidth-lineLen));
                taken.clear();
                taken.push_back(word);
                lineLen = word.size();
            } else{
                taken.push_back(word);
                lineLen += word.size();
            }
        }
        // Last line
        res.push_back(makeLastLine(taken, maxWidth-lineLen));

        return res;
    }
};

