                addLine(res, maxWidth, curLine, false);
                curLine.clear();
                i--;
                curLen = 0;
            }else{
                curLen += words[i].length()+1;
                curLine.add(words[i]);                
            }            
        }
        //add last line 
        addLine(res, maxWidth, curLine, true);
        return res;        
    }
    void addLine(List<String> res, int maxWidth, List<String> curLine, boolean isLastLine){
        StringBuilder sb = new StringBuilder();
        if(isLastLine||curLine.size()==1){
            // left-justified
            for(int i = 0; i < curLine.size(); i++){
                if(i!=0)
                    sb.append(' ');
                sb.append(curLine.get(i));
            }
            while(sb.length()<maxWidth)
                sb.append(' ');
        }else{
            //fully-justified
            int len = maxWidth;
            for(String s: curLine)
                len -= s.length();
            int j = 0;
            j = len % (curLine.size() - 1);
            len = len / (curLine.size() - 1);
            for(int i = 0; i < curLine.size(); i++){
                if(i!=0){
                    for(int k = 0; k < len; k++)
                        sb.append(' ');
                    if(j>0){
                        sb.append(' ');
                        j--;
                    }
                }
                sb.append(curLine.get(i));                
            }                       
        }
        res.add(sb.toString());
    }    
}
