class Solution {
public:
    vector<string> fullJustify(vector<string>& words, int max_width) {

        int n = words.size();

        vector<string> res;

        int i = 0;

        while(i < n)
        {
            int start = i;

            // total characters keeps track of count of included characters

            int total_characters = 0;

            // count_words keeps track of no. of included words

            int count_words = 0;

            // include the words 

            while(i < n && total_characters + count_words + words[i].size() <= max_width)
            {
                total_characters += words[i].size();

                count_words++;

                i++;
            }

            // calculate the no. of spaces we have to insert

            int total_spaces = max_width - total_characters;

            // if no. of words included = 1, then provide all the spaces after the word

            if(count_words == 1)
            {
                string temp_res = "";

                // append the word

                temp_res += words[start];

                // now append the spaces

                string space(total_spaces, ' ');

                temp_res += space;

                res.push_back(temp_res);

                continue;
            }

            // if no. of word is more than 1

            // 1st provide equal spaces between words, then distribute the remaining spaces 

            // no. of places where space will be provided is = total words - 1

            int equal_spaces = total_spaces / (count_words - 1);

            int rem_spaces = total_spaces % (count_words - 1);

            // create a spaces array, which will store the no. of spaces provided between words from left side

            vector<int> spaces(count_words - 1, equal_spaces);

            // distribute the rem spaces from left to right

            int j = 0;

            while(rem_spaces)
            {
                spaces[j++] += 1;

                rem_spaces--;
            }  

            // j will point to the index of spaces array

            j = 0;

            string temp_res = "";

            // 1st case, if the text is not in the last line

            if(i < n)
            {
                for(int k = start; k < i; k++)
                {
                    // push the word into temp res

                    temp_res += words[k];

                    // provide the spaces

                    if(j < spaces.size())
                    {
                        string space(spaces[j++], ' ');

                        temp_res += space;
                    } 
                }
            }

            // second case if the text is in the last line

            else
            {
                for(int k = start; k < i; k++)
                {
                    // push the word into temp res

                    temp_res += words[k];

                    // provide spaces

                    temp_res += ' ';

                    // decrement the spaces

                    total_spaces--;
                }

                // if the total_spaces <= 0, then pop the last extra space

                if(total_spaces <= 0)
                {
                    temp_res.pop_back();
                }

                // otherwise push the spaces into temp_res

                else
                {
                    string space(total_spaces, ' ');

                    temp_res += space;
                } 
            }

            // push the temp_res into res array

            res.push_back(temp_res);
        }

        return res;
    }
};
