  public List<String> fullJustify(String[] words, int maxWidth) {
    var result = new ArrayList<String>();
    // Trick: Use queue and poll, cleaner way to collect & process words. No messy cursors
    // No need to renew too, naturally polled empty
    var wordList = new LinkedList<String>();
    var textWidth = 0;

    for (int i = 0; i < words.length; ) {
      // Need minimum 1 whitespace btw words!
      // Trick: Use wordsToAdd.size to account for whitespaces, instead of +1 to width!
      // Naturally excludes last word's, doesn't include curr count.
      if (textWidth + words[i].length() + wordList.size() <= maxWidth) {
        textWidth += words[i].length();
        wordList.add(words[i]);
        i++;
        continue;
      }

      var spacesInbtw = (maxWidth - textWidth) / Math.max(1, wordList.size() - 1);
      var extraSpaces = (maxWidth - textWidth) % Math.max(1, wordList.size() - 1);

      var line = generateLine(wordList, maxWidth, spacesInbtw, extraSpaces--);
      result.add(line);
      textWidth = 0;
    }

    var line = generateLine(wordList, maxWidth, 1, 0);
    result.add(line);

    return result;
  }

  private String generateLine(LinkedList<String> wordsToAdd, int maxWidth, int spacesBtw, int extraSpaces) {
    var line = new StringBuffer();
    var spaces = addSpaces(spacesBtw);
    while (wordsToAdd.size() > 0) {
      line.append(wordsToAdd.poll())
        .append(spaces)
        .append(extraSpaces-- > 0 ? " " : "");
    }
    // For filling last line
    line.append(addSpaces(maxWidth - line.length()));
    return line.substring(0, maxWidth);
  }

  private String addSpaces(int spaceWidth) {
    var spaces = new StringBuffer();
    while (spaceWidth-- > 0) {
      spaces.append(" ");
    }
    return spaces.toString();
  }
