class Solution {
    public List<String> fullJustify(String[] words, int maxWidth) {
        List<String> output= new ArrayList<>();
        int n= words.length;
        for(int i= 0, k= 1; i<n; i+= k, k= 1){
            // calculate k == how many words we can fit in this line (always at least one) with minimum gap of 1 space
			// and len == what is the total length of these words (then reduced by number of gaps k-1 before passing into printJustified)
			int len= words[i].length();
            for(int j= i+1, nextLen; j<n; j++, k++, len= nextLen)
                if((nextLen= len+1 + words[j].length()) > maxWidth) break;
			// justify left if there is only one word in the line or if it is the last line
            output.add(printJustified(words, maxWidth, i, k, len-k+1, k==1 || i+k == n));
        }
        return output;
    }

    // i: first word, k: word count, len: line length (no spaces), left: left-justified otherwise full
    private String printJustified(String[] words, int maxWidth, int i, int k, int len, boolean left){
        int gapLen= left?1:(maxWidth-len)/(k-1), longGaps= left?0:(maxWidth-len)%(k-1);
        String gap= " ".repeat(gapLen), longGap= " ".repeat(gapLen+1);
        StringBuilder line= new StringBuilder(words[i]);
        for(int j=1; j<k; j++)
            line.append(j<=longGaps?longGap:gap).append(words[i+j]);
        if(left) line.append(" ".repeat(maxWidth-line.length()));
        return line.toString();
    }
}

