        if ( isLastWord ) { // If Last Word - Increase Counter And Break
            ++counter;
            break;
        }
        int nextWordLen = strlen(words[i + 1]); // Get Next Word Length
        int nextLen = len + 1 + nextWordLen;    // Get Summary Length Including Next Word And Spaces Between

        if (nextLen > maxWidth) { // If Length Oversized - Increase Counter
            ++counter;
        }
        len = nextLen > maxWidth ? nextWordLen : nextLen;  // Update Summary Length
    }
    return counter;
}

