{
    int n = 0; // Index For jStr
    const int gaps = wordsSize == 1 ? 1 : wordsSize - 1; // Total Space Gaps
    int spaces = wordsSize-1 + extraSpaces; // Total Spaces To Insert

    for( int i = 0; i<wordsSize; i++) // Iterate Each Word
    {
        while(*words[i]) {
            jStr[n++] = *(words[i]++); // Copy Aa Word To Result String
        }
        if ( n < maxWidth ) {            
            int nSp = spaces / ( gaps - i ) + ( spaces % ( gaps - i ) ? 1 : 0 ); // Calc Number Of Spaces To Insert
            spaces -= nSp;
