/**
 * *Time: O(N^2)
 * *Space: O(N)
 */
// Runtime: 89 ms, faster than 45.91% of JavaScript online submissions for Text Justification.
// Memory Usage: 42 MB, less than 58.01% of JavaScript online submissions for Text Justification.
const fullJustify = (words, maxWidth) => {
	const justifyLine = (line, width) => {
		// if single word, it's left justified, add spaces to the end
		if (line.length === 1) {
			let word = line[0];

			word += ' '.repeat(width);
			return word;

		}

		const end = line.length - 1; // all words except the last word should have spaced added to their string
		let i = 0;

		// distribute spaces evenly among the words
		while (width-- > 0) {
			line[i++] += ' ';
			i = i % end; // loop back to start to avoid adding space to the final word
		}

		return line.join('');
	};

	const output = [];
	let line = [];
	let width = maxWidth; // spaces left

	// greedily add words to each line
	// when current word cannot be added
	// justify and add current line and start a new line
	for (const word of words) {
		// if word fits: add word and update spaces left
		if (word.length <= width - line.length) {
			line.push(word); // word fits: add word to current line
			width -= word.length; // subtract length of current word and update spaces left
		} else {
			// not enough spaces, word does not fit: justify current line and add to output
			const justified = justifyLine(line, width);

			output.push(justified); // add justified line to output
			line = [word]; // start a new line with current word
			width = maxWidth - word.length; // reset current line width
		}
	}

	// handle last line: justify words to the left and add spaces to the right
	if (line.length) {
		let lastLine = line.join(' '); // convert last line into string
		const spacesLeft = maxWidth - lastLine.length; // spaces left for last line

		lastLine += ' '.repeat(spacesLeft);
		output.push(lastLine);
	}

	return output;
};

