public class Solution {
    //很恶心的一道题目，实现挺难
    //主要是3点
    //1. 需要对每个单词中间填上非常平均的空格，这个用line[i % (gaps)].Append(" ");来解决，这里用line.Count-1来得到单词之间的gaps需要多少,然后通过余数来来回给单词中间加上平均的空格
    //Ex This_is_an_ _ _
    //   上面这个例子我们可以看到一个line上总共有3个单词然后需要2个gaps,如何把这些gap均匀分配空格呢，那就是line[i%gaps].Append(" ")
    //2. 当前所有单词的长度加上新单词的长度加上单词之间需要预留的空格如果大于maxWidth那就需要刨去新单词对当前所有单词中gaps做空格均衡
    //3. 最后一行处理的时候直接用string.Join(" ", line)把单词隔开，然后用string.PadRight(maxWidth)把单词做左对齐也就是往右填充足够的空格
    public IList<string> FullJustify(string[] words, int maxWidth) {
        IList<string> res = new List<string>();
        List<StringBuilder> wordsEachLine = new List<StringBuilder>();
        int totalCharsEachLine = 0;
        int wordIndex = 0;
        while(wordIndex < words.Length)
        {
            string word = words[wordIndex];
            wordsEachLine.Add(new StringBuilder(word));
            totalCharsEachLine+=word.Length;
            //先计算当前single gap需要多少个
            // Ex This_is_an  这里就需要2个
            int gaps = wordsEachLine.Count - 1;

            //如果现在line上当前所存在的word加上下一个word和需要的singleGaps数量再加上额外的1（这里因为多加了一个word）长度大于maxWidth，
            //我们就需要往gaps里面添加新的空格来均衡gap的空格数量
            if (wordIndex + 1 < words.Length && totalCharsEachLine + words[wordIndex+1].Length + gaps + 1 > maxWidth)
            {
                int restSpaces = maxWidth - totalCharsEachLine;
                for(int i = 0; i < restSpaces; i++)
                {
                    if (wordsEachLine.Count == 1)
                    {
                        wordsEachLine[0].Append(" ");
                    }
                    else
                    {
                        wordsEachLine[i % gaps].Append(" ");
                    }
                }

                res.Add(string.Join("", wordsEachLine));
                wordsEachLine = new List<StringBuilder>();
                totalCharsEachLine = 0;
            }

            wordIndex++;
        }

        if (wordsEachLine.Count > 0)
        {
            string lastLine = string.Join(" ", wordsEachLine);
            string lastLineRightpad = lastLine.PadRight(maxWidth);
            res.Add(lastLineRightpad);
        }

        return res;
    }
}
