class Solution {
    public List<String> fullJustify(String[] words, int maxWidth) {

        List<String> resText = new ArrayList<>();
        int i = 0;
        while(i < words.length){
            StringBuilder resStr = new StringBuilder(words[i]);

            int j = i + 1;
            int currWidth = words[i].length();
            while(j < words.length && currWidth + 1 + words[j].length() <= maxWidth){
                currWidth += 1 + words[j].length();
                j++;
            }

            // Number of words in the current line are: (excluding the first one)
            int numWords = j - i - 1;

            if(numWords == 0 || j == words.length){
                // Last Line
                for(int k=i+1; k<j; k++){
                    resStr.append(" ");
                    resStr.append(words[k]);
                }

                // Do padding at the end 
                int remLen = maxWidth - resStr.length();
                for(int space=0; space < remLen; space++){
                    resStr.append(" ");
                }

            }else{
                int numSpace = (maxWidth - currWidth)/numWords;
                int extraSpace = (maxWidth - currWidth)%numWords;

                for(int k=i+1; k<j; k++){
                    for(int space=0; space < numSpace; space++){
                        resStr.append(" ");
                    }

                    if(extraSpace > 0){
                        resStr.append(" ");
                        extraSpace--;
                    }

                    resStr.append(" "); // One space was already included 
                    resStr.append(words[k]);
                }

            }

            resText.add(resStr.toString());
            i = j;
        }

        return resText;
    }

}
