public class Solution
{
    public IList<string> FullJustify(string[] words, int maxWidth)
    {
        var lines = new List<string>();

        var left = 0;
        var lineLength = 0;
        var wordsLength = 0;

        for (var i = 0; i < words.Length; i++)
        {
            var word = words[i];
            if (lineLength + word.Length > maxWidth)
            {
                lines.Add(JustifyLine(words, maxWidth, left, i - 1, wordsLength));

                // reset state
                lineLength = 0;
                wordsLength = 0;
                left = i;
            }

            lineLength += word.Length + 1;
            wordsLength += word.Length;
        }

        // yustify the last line
        if (left <= words.Length - 1)
        {
            var line = string.Join(" ", words.Skip(left));
            lines.Add(line + new string(' ', maxWidth - line.Length));
        }

        return lines;
    }

    private string JustifyLine(string[] words, int maxWidth, int start, int end, int wordsLength)
    {
        var builder = new StringBuilder();
        var spaces = maxWidth - wordsLength;

        // justify one word
        if (start == end)
        {
            builder.Append(words[start]);
            builder.Append(' ', spaces);

            return builder.ToString();
        }

        // justify multiple words
        var gaps = end - start;

        for (var i = start; i <= end; i++)
        {
            builder.Append(words[i]);

            // adding spaces if the word isn't last
            if (i != end)
            {
                var count = spaces / gaps + (spaces % gaps == 0 ? 0 : 1);
                builder.Append(' ', count);

                spaces -= count;
                gaps--;
            }
        }

        return builder.ToString();
    }
}
