class Solution {
public:
    vector<string> fullJustify(vector<string>& words, int maxWidth) {
        vector<string> ans;
        int n = words.size();
        string s;
        for(auto it:words) 
        {
            if(s.length() + it.length() > maxWidth) 
            {
                s = s.substr(1);
                s = add_spaces(s, s.length(), maxWidth, maxWidth-s.length());
                ans.push_back(s);
                s = " " + it;
            }
            else 
            {
                s = s + " " + it;
            }
        }
        s = s.substr(1);
        ans.push_back(s);
        string tmp = ans.back();
        ans.pop_back();
        int sz = tmp.size();
        for(int i=0; i<maxWidth-sz; i++) tmp += " ";
        ans.push_back(tmp);
        return ans;
    }

    string add_spaces(string s, int sz, int maxWidth, int extra) 
    {
        int cnt = 0;
        vector<string> v;
        string tmp;

        // count total numbet=r of spaces necessary and storing all the words in given string
        for(int i=0; i<sz; i++) 
        {
            if(s[i] != ' ') tmp += s[i];
            else {
                v.push_back(tmp);
                cnt++;
                tmp = " ";
            }
        }
        v.push_back(tmp);
        extra += cnt;

        int tot = v.size();
        // if there's only one word simply add spaces after the word
        if(cnt == 0) 
        {
            string ans;
            ans += v[0];
            for(int i=0; i<extra; i++) ans += " ";
            return ans;
        }

        string ans;
        // if total spaces are evenly divided between words
        // insert (totalSpaces)/(totalWords-1) spaces between each words
        if(extra % (tot-1) == 0) 
        {
            for(int i=0; i<v.size(); i++) 
            {
                ans += v[i];
                if(i != v.size()-1)
                {
                    for(int j=0; j<(extra/(tot-1))-1; j++) ans +=  " ";
                }
            }
            return ans;
        } 
        else // else insert extra spaces between first (totalSpaces)%(totalWords-1) words
        {
            int extra_space = extra % (tot-1);
            int y = extra / (tot-1);
            for(int i=0; i<v.size(); i++) 
            {
                ans += v[i];
                if(i != v.size()-1)
                {
                    if(extra_space > 0) {
                        for(int j=0; j<y; j++) ans +=  " ";
                        extra_space--;
                    }
                    else for(int j=0; j<y-1; j++) ans +=  " ";
                }
            }
            return ans;
        }
    }
};
