            while(i < words.length && len + words[i].length() <= maxWidth){
                al.add(words[i]);
                len += words[i].length() + 1;
                i++;
            }
            len--;
            int remLen = maxWidth - len;
            if(i == words.length){
                StringBuilder sb = new StringBuilder(al.get(0));
                int j = 1;
                while(j < al.size()){
                    sb.append(" ");
                    sb.append(al.get(j++));
                }
                while(remLen-- > 0)
                    sb.append(" ");
                list.add(sb.toString());
                break;
            } else if(al.size() == 1){
                StringBuilder sb = new StringBuilder(al.get(0));
                while(remLen-- > 0)
                    sb.append(" ");
                list.add(sb.toString());
            } else{
                int div = remLen / (al.size() - 1), rem = remLen % (al.size() - 1);
                StringBuilder sb = new StringBuilder(al.get(0));
                int j = 1;
                while(j < al.size()){
                    sb.append(" ");
                    for(int k = 0; k < div; k++)
                        sb.append(" ");
                    if(rem-- > 0)
                        sb.append(" ");
                    sb.append(al.get(j++));
                }
                list.add(sb.toString());
            }
        }
        return list;
    }
}
