        // step 1: find words that can fit this line
        // step 2: justify
        int start = 0, end = 0;
        vector<string> res;
        while (end < words.size()) {
            int len = 0, total = 0;
            while (end < words.size() && len + words[end].size() <= maxWidth) {
                len += words[end].size() + 1;
                total += words[end].size();
                ++end;
            }
            bool isLast = (end == words.size());
            res.push_back(justify(words, start, end, maxWidth - total, isLast));
            start = end;
        }
        return res;
    }

    string justify(const vector<string>& words, int start, int end, int space, bool isLast) {
        string line;
        if (!isLast) {
            // eg: "Science  is  what we" maxwidth = 20
            // characters: 7 + 2 + 4 + 2 = 15
            // left = 20 - 15 = 5
            // 5/3 => 5 % 3 != 0? => space 1 = 5/3 + 1 = 2
            // 3/2 -> 3 % 2 != 0? => space 2 = 3/2 + 1 = 2
            // 1 => space 3 = 1
			int count = end - start - 1;  // number of space slots = number of words - 1
            for (int i = start; i < end; ++i) {
                line += words[i];
                if (count > 0) {
                    int cur = space % count != 0? space/count + 1: space/count;
                    line.insert(line.end(), cur, ' ');
                    space -= cur;
                    --count;
                }
            }
        } else {
            for (int i = start; i < end; ++i) {
                line += words[i];
                if (space-- > 0) line.push_back(' ');
            }
        }
        if (space > 0) line.insert(line.end(), space, ' ');
