class Solution {
    public void appendSpaces(StringBuilder sb, int spaces) {
        for (int i = 0; i < spaces; i++) {
            sb.append(' ');
        }
    }

    public String makeLeftJustifiedLine(List<String> row, int totalSpaces) {
        StringBuilder sb = new StringBuilder(row.get(0));
        for (int i = 1; i < row.size(); i++) {
            sb.append(' ');
            sb.append(row.get(i));
        }
        appendSpaces(sb, totalSpaces + 1 - row.size());
        return sb.toString();
    }

    public String makeJustifiedLine(List<String> row, int totalSpaces) {
        StringBuilder sb = new StringBuilder(row.get(0));
        if (row.size() <= 1) {
            // only one word in row: left-justify
            appendSpaces(sb, totalSpaces);
        } else {
            int n = row.size() - 1;
            // distribute total spaces among n slots
            int q = totalSpaces / n;
            int r = totalSpaces % n;
            for (int i = 1; i <= n; i++) {
                int spaces = q + (i <= r ? 1 : 0);
                appendSpaces(sb, spaces);
                sb.append(row.get(i));
            }
        }
        return sb.toString();
    }

    public List<String> fullJustify(String[] words, int maxWidth) {
        ArrayList<String> result = new ArrayList<>();
        ArrayList<String> row = new ArrayList<>();
        int charCount = 0;
        for (String word : words) {
            // check if we exceed maxWidth if we add word to the current row
            // remember that for k words, we need at least k-1 spaces
            if (charCount + word.length() + row.size() > maxWidth) {
                result.add(makeJustifiedLine(row, maxWidth - charCount));
                row.clear();
                charCount = 0;
            }
            row.add(word);
            charCount += word.length();
        }
        // last row is left-justified
        result.add(makeLeftJustifiedLine(row, maxWidth - charCount));
        return result;
    }
}
