class Solution:
    def fullJustify(self, words: List[str], max_width: int) -> List[str]:        
        def left_justify(line):
            tmp = " ".join(line)
            return tmp + " " * (max_width - len(tmp))

        def justify(line):
            number_of_spaces = len(line) - 1
            if number_of_spaces == 0:
                return left_justify(line)

            available_space = max_width - sum(len(w) for w in line)
            space_size = available_space // number_of_spaces
            extra = available_space % number_of_spaces

            result = ""
            for i, word in enumerate(line):
                result += word
                if i != len(line) - 1:
                    result += " " * (space_size + int(i < extra))
            return result

        result = []
        current = [words[0]]
        counter = len(words[0])

        for word in words[1:]:
            if counter + 1 + len(word) <= max_width:
                current.append(word)
                counter += 1 + len(word)
            else:
                result.append(justify(current))
                current = [word]
                counter = len(word)

        result.append(left_justify(current))
        return result
