class Solution {
    public List<String> fullJustify(String[] words, int maxWidth) {

        int wordCount = 0;
        int lastIndex = 0;
        int runningLength=0;
        List<String> result = new ArrayList<>();
        for (int i = 0; i < words.length; i++) {
            String s = words[i];
            /**
            * runningLength : total length of words until current word is added
            * s.length() : length of current word
            * wordCount : count of words till now (represents minimum spaces we would need with all the words already include)
            * Adding all of this tells us if current word can fit into current line
            *
            */
            if (runningLength + s.length() + wordCount > maxWidth) {
                //create a line since adding current word crosses the width
                int extraSpace = maxWidth - runningLength;
                // space per word, but not if wordCount is only 1                 
                int spw = wordCount > 1 ? extraSpace / (wordCount - 1) : 0;
                // left over spaces which need to be added 1 after each word until it last
                int los = wordCount > 1 ? extraSpace % (wordCount - 1) : 0;
                // construct line
                result.add(constructLine(words, lastIndex, wordCount, spw, los, maxWidth));
                // reset word count and running length
                wordCount = 0;
                runningLength = 0;
                // update last index to indicate index of word to start new line from
                lastIndex = i;
            }
            // add word length to running length
            runningLength += s.length();             
            wordCount ++;
        }
        // last line
        if (runningLength > 0) {
            result.add(constructLine(words, lastIndex, wordCount, 1, 0, maxWidth));
        }
        return result;
    }

    private String constructLine(String[] words, int li, int n, int spw, int los, int maxWidth) {
        StringBuffer sb = new StringBuffer();
        // start appending words in the line
        for (int i = li; i < li + n - 1; i ++) {
            sb.append(words[i]);
            // append `space per word` spaces after each word, until last word
            for (int j = 0; j < spw; j++) {
                sb.append(" ");
            }
            // append one space from `left over space` after each word, and decrement left over space
            sb.append(los-- > 0 ? " " : "");
        }
        // append last word
        sb.append(words[li + n - 1]);

        // this is specifically for situation when line length still not matches width, append spaces at the end
        // Two situation can lead to this:
        // 1. line only has one word, so we are not adding any space from above logic
        // 2. its last line, so  we are only adding one space per word
        if (sb.length() < maxWidth) {
            int diff = maxWidth - sb.length();
            while (diff-- > 0) {
                sb.append(" ");
            }
        }
        return sb.toString();
    }
}
