                //Reinitializing all the counters for next line
                sum = 0;
                numWords = 0;
                remaining = maxWidth;
                sentence = new ArrayList<>();
            }
        }
        //If the sentence still has words, then leftJustify the sentence
        //Eg: "Leetcode is fun", maxWidth = 20
        //All the words fit on same line, so leftJustify. This is also useful for last line of the output
        if(!sentence.isEmpty()) {
            res.add(leftJustify(sentence, maxWidth));
        }
        return res;
    }

    void calcSpaces(int[] space, int numSpace) {
        int n = space.length;
        Arrays.fill(space, numSpace/n);
        int remaining = numSpace%n;
        int i = 0;
        while(remaining > 0) {
            space[i]++;
            ++i;
            --remaining;
        }
    }

    String leftJustify(List<String> sentence, int maxWidth) {
        StringBuilder sb = new StringBuilder();
        for(String w : sentence) {
            sb.append(w).append(" ");
        }
        //In case the last character of the last word of the sentence occupy maxWidth, we don't need that leading space, so removing it
        sb.deleteCharAt(sb.length()-1);
        //Padding with spaces to reach maxWidth
        while(sb.length() < maxWidth) {
            sb.append(" ");
        }
        return sb.toString();
    }

    String fullJustify(List<String> sentence, int[] space) {
        StringBuilder sb = new StringBuilder();
        for(int i = 0; i < sentence.size()-1; i++) {
            sb.append(sentence.get(i));
            while(space[i] > 0) {
                sb.append(" ");
                space[i]--;
            }
        }
        sb.append(sentence.get(sentence.size()-1));
        return sb.toString();
    }
}
