class Solution {
public:
    vector<string> fullJustify(vector<string>& words, int maxWidth) {
        int i= 0;
        int n= words.size();
        vector<string> result;
        while(i<n){
            int j= i+1;
            int lineLength= words[i].length();

            while(j < n && (lineLength + words[j].length() + (j-i-1))  < maxWidth){
                lineLength= lineLength + words[j].length();
                j++;
            }

            int diff= maxWidth - lineLength;
            int numberOfwords= j-i;
            //Chcek if the line needs to be left justified o r not
            if(numberOfwords == 1  ||  j>=n){ 
                result.push_back(leftJustify(words, diff, i, j));
            }else{
                result.push_back(middleJustify(words, diff, i, j));
            }

            i = j;
        }
        return result;
    }


    string middleJustify(vector<string>& words, int diff, int i, int j){
        string result;
        result = words[i];
        int sections= j-i-1;
        int spaces= diff/sections;
        int extraSpaces= diff % sections;
        for(int k= i+1; k<j; ++k){
            int spacesToApply= spaces + (extraSpaces-- > 0? 1:0);
            while(spacesToApply){
                result= result + " ";
                spacesToApply--;
            }
            result= result + words[k];
        }
        return result;
    }


    string leftJustify(vector<string>& words, int diff, int i, int j){
        string result;
        int spacesInRight= diff - (j-i-1);
        result = words[i];
        for(int k= i+1; k<j; ++k){
            result += " " + words[k];
        }
        while(spacesInRight>0){
            result= result + " ";
            spacesInRight--;
        }
        return result;
    }
};
