    private int findRight(int left, String[] words, int maxWidth) {
        int right = left;
        int sum = words[right].length();
        int n = words.length;
        right++;
        while(right < n && (sum + 1 + words[right].length()) <= maxWidth) {
            sum += 1 + words[right].length(); // add space and new word
            right++;
        }

        return right - 1;
    }

    private String justify(int left, int right, String[] words, int maxWidth) {
        int n = words.length;

        //if only one word
        if (left == right) {
            return words[left] + blanks(maxWidth - words[left].length());
        }

        if (isLastLine(right, n)) {
            String line = getLine(left, right, words);
            return line + blanks(maxWidth - line.length());
        }

        int len = getLineLength(left, right, words);
        int gaps = right - left;
        int spaces = maxWidth - len;
        String fixedSpace = blanks(spaces / gaps);
        int odd = 0;
        if (spaces % gaps != 0) {
            odd = spaces % gaps;
        }


        StringBuilder sb = new StringBuilder();
        for (int i = left; i <= right; i++) {
            sb.append(words[i]).append(fixedSpace);
            if (odd > 0) {
                sb.append(" ");
                odd--;
            }
        }

        return sb.toString().trim();
    }


    private String getLine(int left, int right, String[] words) {
        StringBuilder sb = new StringBuilder();
        for (int i = left; i <= right; i++) {
            sb.append(words[i]).append(" ");
        }

        return sb.toString().trim();
    }

    private int getLineLength(int left, int right, String[] words) {
        int len = 0;
        for (int i = left; i <= right; i++) {
            len += words[i].length();
        }
        return len;
    }



    private String blanks(int length) {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < length; i++) {
            sb.append(' ');
        }

        return sb.toString();
    }
    private boolean isLastLine(int index, int n) {
        return index == n-1;
    }
