class Solution {
public:
    vector<string> fullJustify(vector<string>& words, int maxWidth) {
        int n = words.size();
        int i = 0;
        vector<string> res{};
        string cur = ""; 
        while (i < n) {
            int l = i;
            int sum = 0;
            while (i < n && sum + words[i].size() <= maxWidth) {
                sum += words[i].size() + 1;
                ++i;
            }
            if (i == n) {
                for (int j = l; j < i - 1; ++j) {
                    cur += words[j] + ' ';
                }
                cur += words[n - 1] + string(maxWidth - sum + 1, ' ');
                res.push_back(cur);
            } else {
                int count = 0;
                for (int j = l; j < i - 1; ++j) {
                    int remainder = (maxWidth - sum + i - l) % (i - l - 1);
                    int quotient = (maxWidth - sum + i - l) / (i - l - 1);
                    if (count < remainder) {
                        cur += words[j] + string(quotient + 1, ' ');
                        ++count;
                    } else {
                        cur += words[j] + string(quotient, ' ');
                    }
                }
                cur += words[i - 1];
                cur += string(maxWidth - cur.size(), ' ');
                res.push_back(cur);
                cur = "";
            }
        }
        return res;
    }
};
