class Solution {
public:
    vector<string> fullJustify(vector<string>& words, int maxWidth) {
        int n = words.size();
        int i = 0; // index goes through the words vector
        vector<string> res{}; // return results
        string cur = ""; // string of each result row 
        while (i < n) {
            int l = i; // l is the leftmost index of each row
            int sum = 0;
            // through this while loop, I wnat to find the end index of each row
            // for example, for row "This    is    an", after the wihle loop the 
            // index will be 3
            while (i < n && sum + words[i].size() <= maxWidth) {
                sum += words[i].size() + 1;
                ++i;
            }
            // if i == n, it is the last row, we need only one space between each word
            if (i == n) {
                for (int j = l; j < i - 1; ++j) {
                    cur += words[j] + ' ';
                }
                // add the last word of each row and add the remain space to the end
                cur += words[n - 1] + string(maxWidth - sum + 1, ' ');
                res.push_back(cur);
            } else { // if i != n
                // count counts if the spaces do not divide evenly, then we need to count
                // number of remainder words to add one more space than others
                int count = 0;
                for (int j = l; j < i - 1; ++j) {
                    // for example, "This    is    an"
                    // maxWidth == 16;
                    // sum == (5 + 3 + 3 = 11); each word has one more white space to be counted
                    // i == 3;
                    // l == 0;
                    // then (maxWidth - sum + i - l) == (16 - 11 + 3 - 0 = 8) how many white spaces
                    int remainder = (maxWidth - sum + i - l) % (i - l - 1);
                    int quotient = (maxWidth - sum + i - l) / (i - l - 1);
                    // those words that need one more space than others
                    if (count < remainder) {
                        cur += words[j] + string(quotient + 1, ' ');
                        ++count;
                    } else {
                        cur += words[j] + string(quotient, ' ');
                    }
                }
                // add the last word of each row
                cur += words[i - 1];
                // in case of only one word in a row like "acknowledgment  "
                cur += string(maxWidth - cur.size(), ' ');
                res.push_back(cur);
                // reset cur
                cur = "";
            }
        }
        return res;
    }
};
