class Solution {
    public List<String> fullJustify(String[] words, int maxWidth) {
        List<String> res = new ArrayList();
        int lo = 0;
        while (lo < words.length) { // while we still have next line
            int hi = nextHiIndex(words, lo, maxWidth);
            formatLine(words, lo, hi, maxWidth, res);
            lo = hi;
        }
        return res;
    }

    private int nextHiIndex(String[] words, int lo, int maxWidth) {
        int hi = lo;
        // while next word could fit in
        while (hi < words.length && words[hi].length() <= maxWidth) {
            maxWidth -= words[hi].length(); // reduce word len
            maxWidth--; // reduce one space
            hi++;
        }
        return hi;
    }

    private void formatLine(String[] words, int lo, int hi, int maxWidth, List<String> res) {
        // compute signals needed for format
        List<String> wordsList = Arrays.asList(words).subList(lo, hi);
        int size = hi - lo;
        boolean isLastLine = (hi == words.length);
        boolean isSingleWordLine = (size == 1);
        // format
        if (isSingleWordLine || isLastLine) {
            // "%-30s"
            res.add(String.format("%-" + maxWidth + "s", String.join(" ", wordsList)));
        } else {
            StringBuilder sb = new StringBuilder();
            int wordsLen  = wordsList.stream().map(w -> w.length()).reduce(0, (a, b) -> a + b);
            int spacesLen = maxWidth - wordsLen;
            int avgSpaces = spacesLen / (size - 1);
            int extraSpaces = spacesLen % (size - 1);
            for (int i = lo; i < hi; i++) {
                sb.append(words[i] + String.format("%" + avgSpaces + "s", ""));
                if (extraSpaces-- > 0) sb.append(" ");
            }
            res.add(sb.toString().trim());
        }
    }
}
