class Solution {
private:
    vector<string> getWords(int i, vector<string>& words, int maxWidth) {
        vector<string> currentLine;
        int currLength = 0;

        while (i < words.size() && currLength + words[i].size() <= maxWidth) 
        {
            currentLine.push_back(words[i]);
            currLength += words[i].size() + 1;
            i++;
        }

        return currentLine;
    }

    string createLine(vector<string>& line, int i, vector<string>& words, int maxWidth, bool isLastLine) {
        int baseLength = 0; // Initialize with 0, not -1
        for (string& word : line) {
            baseLength += word.size();
        }

        int extraSpaces = maxWidth - baseLength;

        if (line.size() == 1 || i == words.size() || isLastLine) {
            return joinWordsWithSpaces(line, extraSpaces, true);
        }

        int wordCount = line.size() - 1;
        int spacesPerWord = extraSpaces / wordCount;
        int needsExtraSpace = extraSpaces % wordCount;

        for (int j = 0; j < needsExtraSpace; j++) {
            line[j] += " ";
        }

        for (int j = 0; j < wordCount; j++) {
            line[j] += string(spacesPerWord, ' ');
        }

        return joinWordsWithSpaces(line, 0, false);
    }

    string joinWordsWithSpaces(vector<string>& words, int extraSpaces, bool isLastLine) {
        string result = words[0];
        for (int i = 1; i < words.size(); i++) {
            if (isLastLine) {
                result += " "; // Add a single space between words in the last line
                extraSpaces--;
            } else {
                result += string(extraSpaces > 0 ? 1 : 0, ' ');
                if (extraSpaces > 0) {
                    extraSpaces--;
                }
            }
            result += words[i];
        }
        return result + string(extraSpaces, ' ');
    }
public:
    vector<string> fullJustify(vector<string>& words, int maxWidth) {
        vector<string> ans;
        int i = 0;

        while (i < words.size()) 
        {
            vector<string> currentLine = getWords(i, words, maxWidth);
            i += currentLine.size();
            bool isLastLine = (i == words.size());
            ans.push_back(createLine(currentLine, i, words, maxWidth, isLastLine));
        }

        return ans;
    }
};
