	   n % 26 = 0 % 26 = 0
	   sb.append( (char)(0 + 'A') = 'A');

	   n = n /26 ( 0 / 26 = 0 )
	   // sb = 'CBA'

	   sb.reverse() = 'ABC'

	return "ABC"

