public class Solution {
    public string ConvertToTitle(int columnNumber) {
        var result = new StringBuilder();

        while (columnNumber > 0) {
            // Get the alphabet position of the last character
            var pos = columnNumber % 26;
            if (pos == 0) {
                pos = 26;
            }

            // Convert the position to a uppercase character
            var letter = (char)('A' + (pos - 1));    

            // Save the character at the beginning of the result
            result.Insert(0, letter);

            // Remove the current position from consideration
            columnNumber -= pos;

            // Advance to the next position
            columnNumber /= 26;
        }        

        return result.ToString();
    }
}
```

