class Solution {
    public String convertToTitle(int column) {
		// StringBuilder to store the result
        StringBuilder result = new StringBuilder();

        while (column > 0) {
            int letter = (column % 26); // get letter from the column number
            if (letter == 0) { // edge case for Z (26 % 26 == 0)
                result.append('Z');
                column = (column / 26) - 1;
                continue;
            }
			// regular case
            result.append((char)('A' + (letter - 1)));
            column /= 26;
        }
		// reverse the result and return
        return result.reverse().toString();
    }
}
