char * convertToTitle(int n){

    int numberOfCharacters = 0;
    int input = n;
    while(input > 0){
        input -= 1;
        input /=26;
        numberOfCharacters++;
    }

    //+1 for the null terminator
    char * rv = calloc(numberOfCharacters+1, sizeof(char));

    //filling characters from the back
    int index = numberOfCharacters - 1;
    input = n;
    while(index >= 0){
        input -= 1;
        rv[index--] = (input % 26) + 'A';

        input /=26;
    }

    return rv;
}

