char *convertToTitle(int n) {
    int char_size = 0, num = n, i = 1;
	char *result;
	while(num > 0) {
        num = (num - 1) / 26;
        char_size++;
    }
	result = (char *) malloc(char_size*sizeof(char));
    while(n > 0) {
		if(n % 26 == 0) {
			*(result + char_size - i) = 'Z';
		} else {
			*(result+char_size-i) = (char)('A' + n%26 - 1);
		}
		n = (n - 1) / 26;
		i++;
	}
	return result;
}

