	private int getNumberOfBinaryDigits(int n, long[] powersOf2) {

		for (int i = 1; i < 32; i++) {
			if (powersOf2[i] > n) {
				return i;
			} else if (powersOf2[i] == n) {
				return i + 1;
			}
		}

		return 1000; // never will come here
	}

	private long[] getPowersOf2() {
		long[] powersOf2 = new long[32];

		powersOf2[0] = 1;

		for (int i = 1; i < 32; i++) {
			powersOf2[i] = 2 * powersOf2[i - 1];
		}

		return powersOf2;
	}
}
