class Solution {
public:
    int concatenatedBinary(int n) {
        const int mod = 1e9 + 7;
        long long ans = 0;
        for (int i = 1; i <= n; ++ i) {
            int len = 0;
            for (int j = i; j; j >>= 1) ++ len;
            ans = (ans << len | i) % mod;
        }
        return ans;
    }
};

