class Solution {
public:
    bool buddyStrings(string s, string goal) {
        if(s.length() != goal.length()) return 0;
        if(s == goal) return (set<char>(s.begin(), s.end())).size()<s.length();
        int low =0, high = s.length()-1;
        while(low<=high && s[low] == goal[low]){
            low++;
        }
        while(high >=0 && s[high]== goal[high]){
            high--;
        }
        if(low<high){
            swap(s[low], s[high]);
        }
        return s==goal;
    }
};
```

