/*
situation1:A B different length
situation2:same length&&A equals to B
    if A contains the duplicate letters,after swapping the duplicate letters,A and B are Buddy Strings.
situation3: the rest: same length,A differ from B    
    the ONLY true situation here: A only have 2 different char,A equals to B after swap.                        
*/

class Solution {
     public boolean buddyStrings(String A, String B) {

        int A_length = A.length();
        char Achar[] = A.toCharArray();

        if (A_length != B.length())             //situation1
            return false;

        if(A.equals(B)){                        //situation2										
			Set set1=new HashSet();                   
				for(int i=0;i < A_length ;i++){        
                if(set1.contains(Achar[i]))              
                    return true;                
                else
                    set1.add(Achar[i]);        
            }     
            return false;
        }

                                             // situation3: the rest :
        char Bchar[] = B.toCharArray();
        char A1=0,B1=0;                       // when the 1st Discrepancy happen,A1 is Achar[i], B1 is Bchar[i]
        int j=0;                              //j :count the number of Achar[i]!=Bchar[i]
        for(int i=0; i< A_length &&j<3;i++){                
           if(Achar[i]!=Bchar[i]){                   
                j++;                                           
               if(j==1){
                    A1=Achar[i];               // record Achar[i] and Bchar[i] in A1, B1.
                    B1=Bchar[i];                             
               }                                                                             
               if(j==2 && (Achar[i]!=B1 || Bchar[i]!=A1) )  //when the 2st Discrepancy happen :            
									                        //the only false situation here when j==2
					return false;		       

           }            
        }                       
        return j==2;      //the rest situation:
							    //all the other j==2 situation is true after loop,
								//j=1 false, because unable to swap.
                                //j>2 false, because we can swap only 2 letters.
    }
}
