        while (sArray[l] == goalArray[l] && l < r) {
            l++;
        }

        while (sArray[r] == goalArray[r] && l < r) {
            r--;
        }

        // Swap contents of l and r
        char temp = sArray[l];
        sArray[l] = sArray[r];
        sArray[r] = temp;

        // If after swapping, the goal and s characters  do not match, return false
        if (sArray[l] != goalArray[l] || sArray[r] != goalArray[r]) {
            return false;
