class Solution {
	public boolean buddyStrings(String s, String goal) {
        if (s.length() < 2 ) return false;
		else if (s.replace(String.valueOf(s.charAt(0)), "").length() == 0) return true;
		else if (s.equals(goal)) return Arrays.stream(s.toLowerCase().split("")).distinct().count() != s.length();
		else if (countDiff(s,goal) != 2 ) return false;
		else {
			int[] arr = new int[2];
			int count = 0;
			for (int j = 0; j < s.length(); j++)
				if (s.charAt(j) != goal.charAt(j)) {
					arr[count] = j;
					count++;
				}
			char[] c = s.toCharArray();
			char temp = c[arr[0]];
			c[arr[0]] = c[arr[1]];
			c[arr[1]] = temp;
			return new String(c).equals(goal);
		}
	}

	public static int countDiff(String s, String goal) {
		int count = 0;
		for (int j = 0; j < s.length(); j++)
			if (s.charAt(j) != goal.charAt(j)) count++;
		return count;
	}
}
