import java.util.Set;
import java.util.HashSet;

class Solution {
    public boolean buddyStrings(String A, String B) {
        if (A.length() != B.length()) return false;

        // Idea: find indices where charAt's are different
        int diffIndex1 = -1;
        int diffIndex2 = -1;

        int length = A.length();
        for (int i = 0; i < length; i++) {
            if (A.charAt(i) != B.charAt(i)) {
                if (diffIndex1 == -1) diffIndex1 = i;
                else if (diffIndex2 == -1) diffIndex2 = i;
                else return false;  // More than two differences
            }
        }

        // No differences: return true only if some letter has frequency > 1
        //     since we can just swap that letter with itself
        if (diffIndex1 == -1) {
            final Set<Character> charsSeen = new HashSet<>();
            for (char c : A.toCharArray()) {
                if (charsSeen.add(c) == false) return true;
            }
            return false;
        }

        // Exactly one difference
        if (diffIndex2 == -1) return false;

        // Exactly two differences, see if it can be swapped
        return (A.charAt(diffIndex1) == B.charAt(diffIndex2) && 
                A.charAt(diffIndex2) == B.charAt(diffIndex1));
    }
}
