public boolean buddyStrings(String A, String B) {
	int[] aFreq = new int[26], bFreq = new int[26];
	int count = 0;
	for (int i=0;i<A.length();i++) {
		if(A.charAt(i) != B.charAt(i)) count ++;
		aFreq[A.charAt(i) - 'a']++;
		bFreq[B.charAt(i) - 'a']++;
	}
	boolean hasDuplicates = false;
	for (int i=0;i<26;i++) {
		if(aFreq[i] != bFreq[i]) return false;
		if(aFreq[i] > 1) hasDuplicates = true;
	}
	if(count != 2 && (count != 0 || !hasDuplicates)) return false;
	return true;
}
