def buddyStrings(self, A, B):
        if not A or not B:
            return False
        n, m = len(A), len(B)
        if n < m:
            return False
        if A == B:
            if len(A) % 2 != 0:
                return A[:int(n / 2)] == A[int(n / 2) + 1::]
            else:
                return A[:int(n / 2)] == A[int(n / 2)::]
        for i in range(n):
            if A[i] == B[i]:
                i += 1
            else:
                j = A[i + 1:].find(B[i])
                if j < 0:
                    return False
                else:
                    s = A[i + 1:][j] + A[i + 1:i + j + 1] + A[i] + A[i + j + 2:]
                    if s == B[i:]:
                        return True
                    return False

        return True

def buddyStringsSol2(self, A, B):
	if not A or not B:
		return False
	n, m = len(A), len(B)
	if n != m:
		return False
	if A == B:
		j = int(n / 2) + (1 if len(A) % 2 != 0 else 0)
		return A[:int(n / 2)] == A[j::]
	diffPairs = [(a, b) for a, b in zip(A, B) if a != b]
	if len(diffPairs) > 2:
		return False
	return diffPairs[0] == diffPairs[1][::-1]

def buddyStringsSol3(self, A, B):
	if len(A) != len(B):
		return False
	if A == B and len(set(A)) < len(A):
		return True
	pairs = [(a, b) for a, b in zip(A, B) if a != b]
	return len(pairs) == 2 and pairs[0] == pairs[1][::-1]
