class Solution(object):
    def buddyStrings(self, s, goal):
        ind = []

        for x in range(len(s)):
            if s[x]!=goal[x]:
                ind.append(x)

        if ind:
            s = list(s)

            if len(ind)>1:
                s[ind[0]],s[ind[1]]=s[ind[1]],s[ind[0]]
                s = "".join(s)

                if s==goal:
                    return (True)
                else:
                    return False
            else:
                return False

        elif s==goal:
            for x in s:
                if s.count(x)>1:
                    return (True)
            else:
                return False

