class Solution(object):
    def buddyStrings(self, s, goal):
        if s == goal:
            # Check if s and goal are equal
            # If they are, check if there are any repeated characters in s
            # If there are, we can swap two of them to make s equal to goal
            # Otherwise, we cannot make any swaps, so return False
            return len(set(s)) < len(s)

        if len(s) != len(goal):
            # If the lengths of s and goal are not equal, we cannot make them equal through swaps
            return False

        # Find the indices where s and goal differ
        indices = [i for i in range(len(s)) if s[i] != goal[i]]

        # If there are exactly two differing indices, we can swap the characters at those indices
        if len(indices) == 2:
            i, j = indices
            if s[i] == goal[j] and s[j] == goal[i]:
                return True

        return False

