class Solution {
public:
    bool buddyStrings(string s, string goal) {

        int count = 0; //counts the number of different characters
        int f_ind = -1, s_ind = -1; //stores first index and second index of different characters

        if(s.size()!=goal.size()) return false; //if the two strings are of unequal length, then no swapping can make them equal

        for(int i=0;i<s.size();i++){
            if(s[i]!=goal[i])
            {
                count++;
                if(count>2) return false; //more than two different characters are present
                if(f_ind==-1) f_ind=i;
                else s_ind=i;
            }
        }

        if(count == 0){ //equal strings
            int freq[26] = {0};
            for(int i=0;i<s.size();i++){
                freq[s[i]-'a']++;
                if(freq[s[i]-'a']==2){
                    return true; //since we found two same elements which can be swapped
                }
            }
            return false;
        }

        //only one unequal character is present which cannot be swapped with any other
        else if(count==1) 
            return false;

        swap(s[f_ind], s[s_ind]);
        if(s==goal) return true;
        else return false;

    }
};
```


