use std::collections::HashSet;
impl Solution {
    pub fn min_remove_to_make_valid(s: String) -> String {
        let mut S = vec![];
        let mut R = HashSet::new();
        for (i, c) in s.chars().enumerate() {
            if c == '(' {
                S.push(i);
            }
            if c == ')' {
                if 0 < S.len() {
                    S.pop();
                } else {
                    R.insert(i);
                }
            }
        }
        let L: HashSet<usize> = S.drain(..).collect();
        return s.chars().enumerate().filter(|(i, c)| !L.contains(i) && !R.contains(i)).map(|(i, c)| c).collect();
    }
}
```

