var minRemoveToMakeValid = (s, t=[], del=new Set(), stack=[]) => {
    for (let i=0; i < s.length; ++i) {
        if (s[i] == '(')
            stack.push(i);
        if (s[i] == ')') {
            if (stack.length == 0)
                del.add(i);
            else
                stack.pop();
        }
    }
    stack.forEach(i => del.add(i));
    for (let i=0; i < s.length; ++i)
        if (!del.has(i))
            t.push(s[i]);
    return t.join('');
};
```

