        for (auto i=0; i < s.size(); ++i) {
            if (s[i] == '(')
                stack.push_back(i);
            if (s[i] == ')') {
                if (stack.empty())
                    del.insert(i);
                else
                    stack.pop_back();
            }
        }
        del.insert(stack.begin(), stack.end());
        for (auto i=0; i < s.size(); ++i)
            if (del.find(i) == del.end())
                t.push_back(s[i]);
        return t;
    }
};
