class Solution(object):
    def minAddToMakeValid(self, S):
        """
        :type S: str
        :rtype: int
        """
        stack =[]
        for i in S:
            if i=="(":
                stack.append(i)

            else:
                if i == ")":

                    if stack and stack[-1]=="(":
                        stack.pop()
                    else:
                        stack.append(i)

        return len(stack)
