class Solution(object):
    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        stack =[]

        cur_long = 0
        max_long = 0

        for i in s:
            if i == "(":
                stack.append(cur_long)
                cur_long =0
            else:
                if stack:
                    cur_long += stack.pop() + 2
                    max_long = max(cur_long, max_long)
                else:
                    cur_long = 0

        return max_long
