func minRemoveToMakeValid(s string) string {
	stck := []int{}
	bs := []byte(s)
	for i, b := range bs {
		if b == '(' {
			stck = append(stck, i)
		} else if b == ')' {
			if len(stck) > 0 {
				stck = stck[:len(stck)-1]
			} else {
				bs[i] = '*'
			}
		}
	}
	for len(stck) > 0 {
		bs[stck[len(stck)-1]] = '*'
		stck = stck[:len(stck)-1]
	}
	return strings.ReplaceAll(string(bs), "*", "")
}
